const initialState = {
  infoUser: null,
};
const UserReducer = (state = initialState, action) => {
  switch (action.type) {
    case "SAVE_USER_LOGIN_SUCCESS": {
      return { ...state, infoUser: action.payload };
    }
    default:
      return { ...state };
  }
};
export default UserReducer;
