import React, { useState } from "react";
import { useSelector } from "react-redux";
import SelectMenuDateItem from "../SelectMenuDateItem";
import "./SelectMenuDate.scss";

function SelectMenuDate(props) {
  let [date, setDate] = useState(-1);
  let listDate = useSelector((state) => {
    return state.filmReducer?.Onerap;
  });
  let statustDate = -1;
  statustDate =
    statustDate ||
    useSelector((state) => {
      return state.filmReducer.statusDate;
    });
  // console.log(listDate?.lichChieuPhim);
  function showListDate() {
    return listDate?.lichChieuPhim.map((item, index) => {
      return (
        <SelectMenuDateItem
          item={item}
          key={index}
          showDateOnMenu={showDateOnMenu}
        />
      );
    });
  }
  // function setStatusDate(date){

  // }
  function showDateOnMenu(d) {
    statustDate = date;
    setDate(statustDate);
    setDate(d);
  }
  return (
    <div className="dropdown dropdown--fixed wp__timeSeeing">
      <button
        className=" btn  dropdown-toggle dropdown-toggle--fixed"
        type="button"
        id="dropdownMenuButton"
        data-toggle="dropdown"
        aria-expanded="false"
      >
        <span className="timeSeeing__movieDropdown">
          {statustDate === -1 ? "Ngày xem" : date}
        </span>
        <i className="fas fa-angle-down" />
      </button>
      <ul
        className="dropdown-menu dropdown-menu--fixed animate slideIn dr-menu--listDate"
        aria-labelledby="dropdownMenuButton"
      >
        {showListDate()}
      </ul>
    </div>
  );
}

export default SelectMenuDate;
