import React, { useEffect, useRef, useState } from "react";
import SelectMenuFilm from "../SelectMenuFilm";
import { GetListMovieTheaterByIdRequest } from "../../redux/actions/getListMovieTheatersById";
import "./selectMenu.scss";
import { useDispatch } from "react-redux";
import SelectMenuMovieTheater from "../SelectMenuMovieTheater";
function SelectMenu(props) {
  let dispatch1 = useDispatch();
  let [idFilm, setIdFilm] = useState(-1);
  function getIdFilm(id) {
    setIdFilm(id);
    useEffect(() => {
      return () => {};
    }, []);
  }
  function usePrevious(value) {
    const ref = useRef();
    useEffect(() => {
      ref.current = value;
    });
    return ref.current;
  }
  const previousIdFilm = usePrevious(idFilm);
  useEffect(() => {
    dispatch1(GetListMovieTheaterByIdRequest(idFilm));
  }, [idFilm]);
  return (
    <section className="selectMenu">
      <SelectMenuFilm getIdFilm={getIdFilm} />
      <SelectMenuMovieTheater idFilm={idFilm} previousIdFilm={previousIdFilm} />

      <div className="dropdown dropdown--fixed wp__timeSeeing">
        <button
          className=" btn  dropdown-toggle dropdown-toggle--fixed"
          type="button"
          id="dropdownMenuButton"
          data-toggle="dropdown"
          aria-expanded="false"
        >
          <span className="timeSeeing__movieDropdown">Ngày xem</span>
          <i className="fas fa-angle-down" />
        </button>
        <ul
          className="dropdown-menu dropdown-menu--fixed animate slideIn"
          aria-labelledby="dropdownMenuButton"
        >
          <li className="dropdown-item">Action</li>
          <li className="dropdown-item">Another action</li>
        </ul>
      </div>

      <div className="dropdown dropdown--fixed wp__CinemaInterest">
        <button
          className=" btn  dropdown-toggle dropdown-toggle--fixed"
          type="button"
          id="dropdownMenuButton"
          data-toggle="dropdown"
          aria-expanded="false"
        >
          <span className="CinemaInterest__movieDropdown">Suất chiếu</span>
          <i className="fas fa-angle-down" />
        </button>
        <ul
          className="dropdown-menu dropdown-menu--fixed animate slideIn"
          aria-labelledby="dropdownMenuButton"
        >
          <li className="dropdown-item">Action</li>
          <li className="dropdown-item">Another action</li>
        </ul>
      </div>
      <button className="btn_bookingTicketNow">Mua vé ngay</button>
    </section>
  );
}

export default SelectMenu;
