import React, { useState } from "react";
import { useSelector } from "react-redux";
import SelectMenuMovieTheaterItem from "../SelectMenuMovieTheaterItem";
import "./SelectMenuMovieTheater.scss";
function SelectMenuMovieTheater(props) {
  let [rap, setRap] = useState("Rạp");
  // let [statusRap, setStatusRap] = useState(true);
  let listCinemaTheater = useSelector((state) => {
    return state.filmReducer.CinemaSystem;
  });
  let statusCinemaTheater = useSelector((state) => {
    return state.filmReducer.statusCinemaSystem;
  });
  // console.log(listCinemaTheater);
  let CumRapArr = [];
  if (listCinemaTheater.heThongRapChieu !== undefined) {
    let CumRapChieu = listCinemaTheater.heThongRapChieu.map((item, index) => {
      return item.cumRapChieu;
    });
    for (let i = 0; i < CumRapChieu.length; i++) {
      CumRapChieu[i].map((item, index) => {
        // console.log(item);
        CumRapArr.push(item.tenCumRap);
      });
    }
  }
  // console.log(props.idFilm);
  // console.log(listCinemaTheater.maPhim);
  // console.log(props.statusFilm);
  function showNameRap(nameRap, status) {
    // console.log("ok 3");
    // console.log(props.statusFilm);
    if (
      // Vì lúc đầu khi nhấn chọn Film thì ta setstatusFilm là true nên ta phải kiểm tra thêm 1 điều kiện nữa là nó
      //nó phải được nhấn lúc đầu ( vì lúc đầu ta có id film = -1 giá trị khởi tạo)
      // khi nhấn lần tiếp theo thì ta đã set statusFilm là false nên ko cần set theeo đk id
      (props.statusFilm === true && props.preIdFilm === -1) ||
      props.statusFilm === false ||
      status === false
    ) {
      // console.log("ok 1");
      setRap(nameRap);
    }
  }
  // console.log(CumRapArr);
  function ShowListCumRapChieu() {
    if (CumRapArr !== undefined && CumRapArr.length > 0) {
      //   console.log(CumRapArr);
      return CumRapArr.map((item, index) => {
        return (
          <SelectMenuMovieTheaterItem
            item={item}
            key={index}
            showNameRap={showNameRap}
            getRap={props.getRap}
          />
        );
      });
    }
  }

  return (
    <div className="dropdown dropdown--fixed wp__movieTheaterDropdown">
      <button
        className=" btn  dropdown-toggle dropdown-toggle--fixed"
        type="button"
        id="dropdownMenuButton"
        data-toggle="dropdown"
        aria-expanded="false"
      >
        <span className="theater__movieDropdown">
          {props.statusFilm === true && props.preIdFilm !== -1 ? "Rạp" : rap}
        </span>
        <i className="fas fa-angle-down" />
      </button>
      <ul
        className="dropdown-menu animate slideIn dr-menu--listTheater"
        aria-labelledby="dropdownMenuButton"
      >
        {ShowListCumRapChieu()}
      </ul>
    </div>
  );
}

export default SelectMenuMovieTheater;
