const initialState = {
  listFilm: [],
  CinemaSystem: [],
  Onerap: null,
  statusDate: null,
};
const filmReducer = (state = initialState, action) => {
  switch (action.type) {
    case "GET_LIST_FILM_SUCCESS": {
      return { ...state, listFilm: action.payload };
    }
    case "GET_MOVIETHEATER_SUCCESS": {
      return { ...state, CinemaSystem: action.payload };
    }
    case "GET_DATE_SHOWTIME": {
      // console.log(state.CinemaSystem.heThongRapChieu);
      let htRap = state.CinemaSystem.heThongRapChieu?.map((item, index) => {
        return item;
      });
      let cumRap = htRap.map((item, index) => {
        return item.cumRapChieu;
      });
      console.log(cumRap);
      // console.log(action.payload);
      // let oneRap = cumRap.map((item) => {
      //   return item.filter((itemList, index) => {
      //     return itemList.tenCumRap === action.payload;
      //   });
      // });
      // console.log(oneRap);
      // state.Onerap = oneRap;
      // state.statusDate = action.status;
      // console.log(state.Onerap);
      return { ...state };
    }
    case "CHANGE_STATUS_DATE_BY_THEATER": {
      state.statusDate = action.status;
      return { ...state };
    }
    case "CHANGE_STATUS_DATE_BY_LIST_FILM": {
      state.statusDate = action.status;
      return { ...state };
    }
    default:
      return { ...state };
  }
};
export default filmReducer;
