const initialState = {
  CinemaSys: [],
  InfoShowTimeEachSys: [],
  CinemaSysSelected: null,
  ListCinemaBrands: [],
  CinemaBrandSelected: null,
  ListFilm: [],
};
const CinemaHome = (state = initialState, action) => {
  switch (action.type) {
    case "GET_CINEMASYS_SUCCESS": {
      return { ...state, CinemaSys: action.payload };
    }
    case "GET_LIST_CINEMA_BRAND_SUCCESS": {
      console.log(action.payload);
      // let newListRap = [...action.payload];

      return {
        ...state,
        ListCinemaBrands: [...action.payload],
        // CinemaBrandSelected: newListRap[0].maCumRap,
      };
    }
    case "GET_INFO_SHOW_TIME_EACH_SYS_SUCCESS": {
      console.log(action.payload[0]);
      // console.log(state.CinemaBrandSelected);
      let dsCumRap = action.payload[0].lstCumRap;
      console.log(dsCumRap);
      // console.log(dsPhim[0].danhSachPhim);
      return { ...state, InfoShowTimeEachSys: action.payload };
    }
    default:
      return { ...state };
  }
};
export default CinemaHome;
