import React, { useState } from "react";
import "./CinemaSysHomeLeftItem.scss";
function CinemaSysHomeLeftItem(props) {
  let [opacity, setOpacity] = useState(false);
  let className = "img-fluid";
  function handleClickCinemaSys() {
    setOpacity(true);
  }
  let { item } = props;
  return (
    <li className="tab__item" onClick={handleClickCinemaSys}>
      <a className="tab__item__SysLink">
        <img
          src={item.logo}
          className={opacity ? active__opacity : "img-fluid"}
          alt="anh"
        />
      </a>
    </li>
  );
}
export default CinemaSysHomeLeftItem;
