import React, { useEffect, useRef, useState } from "react";
import SelectMenuFilm from "../SelectMenuFilm";
import { GetListMovieTheaterByIdRequest } from "../../redux/actions/getListMovieTheatersById";
import "./selectMenu.scss";
import { useDispatch } from "react-redux";
import SelectMenuMovieTheater from "../SelectMenuMovieTheater";
import SelectMenuDate from "../SelectMenuDate";
import SelectMenuTime from "../SelectMenuTime";
function SelectMenu(props) {
  let dispatch1 = useDispatch();
  let [idFilm, setIdFilm] = useState(-1);
  //mac dinh la false thi dang o phim hien tai va duoc chon rap o phim hien tai
  //nếu statusFilm là true tức ta đã chọn film mới và ta sẽ reset cụm rạp theo tên phim mới
  let [statusFilm, setstatusFilm] = useState(false);
  function getIdFilm(id) {
    setIdFilm(id);
    setstatusFilm(true);
  }
  function usePrevious(value) {
    const ref = useRef();
    useEffect(() => {
      ref.current = value;
    });
    return ref.current;
  }
  let preIdFilm = usePrevious(idFilm);
  useEffect(() => {
    dispatch1(GetListMovieTheaterByIdRequest(idFilm));
  }, [idFilm]);
  return (
    <section className="selectMenu">
      <SelectMenuFilm getIdFilm={getIdFilm} />
      <SelectMenuMovieTheater
        preIdFilm={preIdFilm}
        idFilm={idFilm}
        statusFilm={statusFilm}
      />

      <SelectMenuDate />
      <SelectMenuTime />
      <button className="btn_bookingTicketNow">Mua vé ngay</button>
    </section>
  );
}

export default SelectMenu;
