import React from "react";
import { useDispatch } from "react-redux";
import { activeSeat } from "../../../redux/actions/Booking";
function renderTypeSeat(daDat, dangChon) {
  if (daDat) {
    return "seat__item__disable";
  } else {
    if (dangChon) {
      return "sp__seat__selected";
    } else {
      return "sp__seat__normal";
    }
  }
}
function renderSeatVip(loaiGhe) {
  if (loaiGhe === "Vip") {
    return "seat__item__vip";
  }
}
function SeatItem(props) {
  let dispatch = useDispatch();
  // console.log(props.seatItem);
  let dangChon = false;
  let {
    maGhe,
    tenGhe,
    maRap,
    loaiGhe,
    stt,
    giaVe,
    daDat,
    taiKhoanNguoiDat,
  } = props.seatItem;
  let code = props.code;
  function ActiveSeatSelected(ghe) {
    let dangChon = true;
    let codeSeat = code + tenGhe;
    let seatSelecting = {
      ...props.seatItem,
      codeSeat,
      selectingSeat: dangChon,
    };
    dispatch(activeSeat(seatSelecting));
  }
  return (
    <React.Fragment>
      <span
        className="seat__wrapper"
        onClick={() => ActiveSeatSelected(props.seatItem)}
      >
        <span
          style={{ color: "white" }}
          className={`seat__item ${renderSeatVip(loaiGhe)} ${renderTypeSeat(
            daDat,
            dangChon
          )}`}
        >
          {tenGhe}
        </span>
      </span>
    </React.Fragment>
  );
}

export default SeatItem;
