const initialState = {
  bookingInfo: null,
  listSeat: [],
  total: 0,
};

const BookingReducer = (state = initialState, action) => {
  switch (action.type) {
    case "GET_BOOKING_SUCCESS": {
      let newBookingInfo = { ...action.payload };
      let newListSeat = [...action.payload.danhSachGhe];
      return { ...state, bookingInfo: newBookingInfo };
    }
    case "CHOOSE_SEAT": {
      console.log(action.payload);
      console.log(state.bookingInfo);
      return { ...state };
    }
    default:
      return { ...state };
  }
};
export default BookingReducer;
