import React, { useEffect } from "react";
import "react-owl-carousel2/lib/OwlCarousel";
import "react-owl-carousel2/src/owl.carousel.css";
import "react-owl-carousel2/src/owl.theme.default.css";
import OwlCarousel from "react-owl-carousel2";
import "./TabFilmShowing.scss";
import ListFilm from "../ListFilm";
import { useDispatch, useSelector } from "react-redux";
import { GetListMovieRequest } from "../../redux/actions/GetListMovie";
function TabFilmShowing(props) {
  const options = {
    items: 1,
    nav: true,
    rewind: true,
    dots: false,
    autoplay: true,
    smartSpeed: 500,
  };
  let dispatch = useDispatch();
  useEffect(() => {
    dispatch(GetListMovieRequest());
  }, []);
  let listMovie = useSelector((state) => {
    return state.Movie.listMovie;
  });
  // console.log(listMovie);
  function showPageSlider() {
    let rows = [];
    let totalPage = Math.ceil(listMovie.length / 8);
    console.log(totalPage);
    for (let i = 1; i < totalPage; i++) {
      rows.push(<ListFilm id={totalPage[i]} />);
    }
  }
  return (
    <div className="tab-pane fade show active" id="Showing" role="tabpanel">
      <OwlCarousel options={options}>{showPageSlider()}</OwlCarousel>
    </div>
  );
}

export default TabFilmShowing;
