const initialState = {
  bookingInfo: null,
  listSeat: [],
  total: 0,
};

const BookingReducer = (state = initialState, action) => {
  switch (action.type) {
    case "GET_BOOKING_SUCCESS": {
      let newBookingInfo = { ...action.payload.thongTinPhim };
      let newListSeat = [...action.payload.danhSachGhe];
      return { ...state, bookingInfo: newBookingInfo, listSeat: newListSeat };
    }
    case "CHOOSE_SEAT": {
      console.log(action.payload);
      const index = state.listSeat.findIndex(
        (ghe) => ghe.maGhe === action.payload.maGhe
      );
      state.listSeat[index] = { ...action.payload };
      const newListSeat = [...state.listSeat];
      return { ...state, listSeat: newListSeat };
    }
    default:
      return { ...state };
  }
};
export default BookingReducer;
