import React from "react";

function renderTypeSeat(daDat, dangChon) {
  if (daDat) {
    return "seat__item__disable";
  } else {
    if (dangChon) {
      return "sp__seat__selected";
    } else {
      return "sp__seat__normal";
    }
  }
}
function renderSeatVip(loaiGhe) {
  if (loaiGhe === "Vip") {
    return "seat__item__vip";
  }
}
function SeatItem(props) {
  console.log(props.seatItem);
  let dangChon = false;
  let {
    daDat,
    giaVe,
    loaiGhe,
    maGhe,
    maRap,
    stt,
    taiKhoanNguoiDat,
    tenGhe,
  } = props.seatItem;
  let code = props.code;
  function ActiveSeatSelected(_daDat, _giaVe, _maGhe, _tenGhe, _code) {
    let dangChon = true;
    let seatSelected = {
      selected: _daDat,
      price: _giaVe,
      codeSeat: _maGhe,
      nameSeat: _tenGhe,
      code: _code,
      dangChon: dangChon,
    };
  }
  return (
    <React.Fragment>
      <span
        className="seat__wrapper"
        onClick={() => ActiveSeatSelected(daDat, giaVe, maGhe, tenGhe, code)}
      >
        <span
          style={{ color: "white" }}
          className={`seat__item ${renderSeatVip(loaiGhe)} ${renderTypeSeat(
            daDat,
            dangChon
          )}`}
        >
          {tenGhe}
        </span>
      </span>
    </React.Fragment>
  );
}

export default SeatItem;
