import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import style from "./Login.module.scss";
function Login(props) {
  let [userLogin, setUserLogin] = useState({
    values: { taiKhoan: "", matKhau: "" },
    errors: { taiKhoan: "", matKhau: "" },
  });
  function handleChange(e) {
    let tagInput = e.target;
    // console.log(tagInput);
    let { name, value } = tagInput;
    console.log(name, value);
    let errorMessage = "";
    let values = { ...userLogin.values, [name]: value };
    let errors = { ...userLogin.values, [name]: errorMessage };
  }
  return (
    <div className={style.wrapper__login}>
      <form action="" className={style.form}>
        <img
          className="mb-5"
          src="../../../images/logoLogin.png"
          alt="logo"
          style={{ width: "180px" }}
        />
        <div className="row">
          <div className="col-md-12 mb-4">
            <div className={(style.form__group, style.field)}>
              <input
                type="text"
                className={style.form__field}
                placeholder="taiKhoan"
                name="taiKhoan"
                id="taiKhoan"
                required
                onChange={handleChange}
              />
              <label htmlFor="taiKhoan" className={style.form__label}>
                username
              </label>
            </div>
            <span className="text-danger">{userLogin.errors.taiKhoan}</span>
          </div>

          <div className="col-md-12 my-3">
            <div className={(style.form__group, style.field)}>
              <input
                type="password"
                className={style.form__field}
                placeholder="matKhau"
                name="matKhau"
                id="matKhau"
                required
                onChange={handleChange}
              />
              <label htmlFor="matKhau" className={style.form__label}>
                password
              </label>
            </div>
            <span className="text-danger">{userLogin.errors.matKhau}</span>
          </div>
        </div>
        <button className="btn btn-danger w-100 mt-3">Đăng nhập</button>
        <NavLink className={style.link__Reg} to="/Reg">
          Đăng ký
        </NavLink>
      </form>
    </div>
  );
}

export default Login;
