import React from "react";
import format from "date-format";
import { useDispatch } from "react-redux";
import { changeStatusDateByTheater } from "../../redux/actions/getDateShowTime";
import { getTimeShowTime } from "../../redux/actions/getTimeShowTime";
function SelectMenuDateItem(props) {
  let dispatch = useDispatch();
  // console.log(props.item);
  let { item } = props;
  function handleClick() {
    dispatch(
      changeStatusDateByTheater(
        format("yyyy-MM-dd", new Date(item.ngayChieuGioChieu))
      )
    );
    dispatch(
      getTimeShowTime(
        format("yyyy-MM-dd-hh:mm", new Date(item.ngayChieuGioChieu)),
        format("hh:mm", new Date(item.ngayChieuGioChieu))
      )
    );
  }
  return (
    <li
      className="dropdown-item dropdown-item--listTheater"
      onClick={handleClick}
    >
      {format("yyyy-MM-dd", new Date(item.ngayChieuGioChieu))}
    </li>
  );
}

export default SelectMenuDateItem;
