import React, { useEffect, useState } from "react";
import CinemaSysHomeLeft from "../CinemaSysHomeLeft";
import CinemaSysHomeMiddle from "../CinemaSysHomeMiddle";
import CinemaSysHomeRight from "../CinemaSysHomeRight";
import { GetListCinemaBrandRequest } from "../../redux/actions/CinemaBrand";
import "./CinemaSysHome.scss";
import { useDispatch, useSelector } from "react-redux";
function CinemaSysHome(props) {
  let listCinemaBrandOfEachSys = useSelector((state) => {
    return state.CinemaHome.ListCinemaBrands;
  });
  // console.log(listCinemaBrandOfEachSys);
  let maCumRap = listCinemaBrandOfEachSys?.map((item, index) => {
    return item.maCumRap;
  });
  console.log(maCumRap[0]);
  let [idlogo, setidlogo] = useState("BHDStar");
  let [idbrand, setidbrand] = useState(maCumRap[0]);
  let dispatch = useDispatch();
  function setIdLogo(id) {
    setidlogo(id);
  }
  console.log(maCumRap);
  useEffect(() => {
    dispatch(GetListCinemaBrandRequest(idlogo));
  }, [idlogo]);
  return (
    <div className="wrapper__systemCinema--fixed">
      <div className="row">
        <div className="col-md-12">
          <div className="bg__back-news" />
        </div>
      </div>
      <div className="row">
        <div className="col-md-12">
          <div className="wrapper__tab">
            <h3>hệ thống rạp</h3>
            <CinemaSysHomeLeft setIdLogo={setIdLogo} />
            <CinemaSysHomeMiddle />
            <CinemaSysHomeRight idbrand={idbrand} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default CinemaSysHome;
