import React, { useState } from "react";
import "./CinemaSysHomeLeftItem.scss";
function CinemaSysHomeLeftItem(props) {
  let { item, idx } = props;
  let [opacity, setOpacity] = useState(status);
  //   let className = "img-fluid";
  function handleClickCinemaSys() {
    setOpacity(!status);
  }
  function handleOpacity() {
    // setOpacity(true);
    if (opacity) {
      return "tab__item active__opacity";
    } else {
      return "tab__item";
    }
  }

  console.log(idx);
  return (
    <li className={handleOpacity()} onClick={handleClickCinemaSys}>
      <a className="tab__item__SysLink">
        <img src={item.logo} className="img-fluid" alt="anh" />
      </a>
    </li>
  );
}
export default CinemaSysHomeLeftItem;
