const initialState = {
  CinemaSys: [],
  InfoShowTimeEachSys: [],
  CinemaSysSelected: null,
  ListCinemaBrands: [],
  CinemaBrandSelected: null,
  ListFilm: [],
};
const CinemaHome = (state = initialState, action) => {
  switch (action.type) {
    case "GET_CINEMASYS_SUCCESS": {
      return { ...state, CinemaSys: action.payload };
    }
    case "GET_LIST_CINEMA_BRAND_SUCCESS": {
      return {
        ...state,
        ListCinemaBrands: action.payload,
        CinemaBrandSelected: action.payload[0].maCumRap,
      };
    }
    case "GET_INFO_SHOW_TIME_EACH_SYS_SUCCESS": {
      let dsPhim = action.payload[0].lstCumRap;
      // console.log(dsPhim);
      console.log(dsPhim[0].danhSachPhim);
      return {
        ...state,
        InfoShowTimeEachSys: action.payload,
        ListFilm: dsPhim[0].danhSachPhim,
      };
    }
    case "GET_INFO_SHOW_TIME_EACH_SYS_FAILED": {
      let emptyArrListFilm = [];
      return { ...state, ListFilm: emptyArrListFilm };
    }
    default:
      return { ...state };
  }
};
export default CinemaHome;
