const initialState = {
  CinemaSys: [],
  InfoShowTimeEachSys: [],
  CinemaSysSelected: null,
  ListCinemaBrands: [],
  CinemaBrandSelected: null,
  ListFilm: [],
};
const CinemaHome = (state = initialState, action) => {
  switch (action.type) {
    case "GET_CINEMASYS_SUCCESS": {
      return { ...state, CinemaSys: action.payload };
    }
    case "GET_LIST_CINEMA_BRAND_SUCCESS": {
      return {
        ...state,
        ListCinemaBrands: action.payload,
        CinemaBrandSelected: action.payload[0].maCumRap,
      };
    }
    case "GET_INFO_SHOW_TIME_EACH_SYS_SUCCESS": {
      return { ...state, InfoShowTimeEachSys: action.payload };
    }
    case "GET_LIST_FILM_BY_ID_CINEMA_BRAND": {
      // console.log(action.payload);
      console.log(state.InfoShowTimeEachSys);
      let infoOneBrand = state.InfoShowTimeEachSys[0]?.lstCumRap?.filter(
        (item, index) => {
          return item.maCumRap === state.CinemaBrandSelected;
        }
      );
      let listMovie = infoOneBrand?.map((item, index) => {
        return item.danhSachPhim;
      });
      console.log(listMovie);
      // state.ListFilm = [...listMovie];
      return { ...state };
    }
    default:
      return { ...state };
  }
};
export default CinemaHome;
