import React, { useState } from "react";
import "./CinemaSysHomeLeftItem.scss";
function CinemaSysHomeLeftItem(props) {
  let { item, idx, maHeThong } = props;
  let [opacity, setOpacity] = useState("");
  //   let className = "img-fluid";
  function handleClickCinemaSys(e) {
    let Idx = e.currentTarget.id;
    console.log(Idx);
    setOpacity(Idx.toString());
    console.log(opacity);
    // if (opacity !== Idx) {
    // }
  }
  console.log(idx);
  return (
    <li
      className={
        opacity === maHeThong ? "tab__item active__opacity" : "tab__item"
      }
      onClick={handleClickCinemaSys}
      id={maHeThong.toString()}
    >
      <a className="tab__item__SysLink">
        <img src={item.logo} className="img-fluid" alt="anh" />
      </a>
    </li>
  );
}
export default CinemaSysHomeLeftItem;
